class Solution {
public:
    const double PI = 2*acos(0.0);

    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        // convert point to polar coordinates
        int alwaysVisible = 0;
        vector<double> polar;
        float radians = PI * double(angle) / 180.0;
        for (int i = 0; i < points.size(); ++i) {
            // handle atan asymptotes along the y axis
            if (points[i][0] == location[0]) {
                if (points[i][1] > location[1])
                    polar.push_back(PI);
                else if (points[i][1] < location[1])
                    polar.push_back(0.0);
                else alwaysVisible++;
            } else {
                double vectorX = points[i][0] - location[0], vectorY = points[i][1] - location[1];                
                double length = sqrt(vectorX * vectorX + vectorY * vectorY);
                vectorX /= length, vectorY /= length;
                double angle = atan(vectorY / vectorX);
                // map angles to a continuous range from 0 to 2*pi, starting from the bottom and running counter clockwise.
                if (points[i][0] < location[0])
                    angle += PI * 1.5; // map left side from -pi/2..pi/2 to pi..2*pi
                else angle += PI * 0.5; // map right side from -pi/2..pi/2 to 0..pi 
                polar.push_back(angle);
            }
        }
        sort(polar.begin(), polar.end());
        int maxVisible = alwaysVisible;
        int pointsInRange = 1;
        for (int i = 0; i < polar.size(); ++i) {
            // align our "camera"'s left side with point i
            bool done = false;
            while (pointsInRange < polar.size() && !done) {
                double diff = polar[(i + pointsInRange) % polar.size()] - polar[i];
                if (diff < 0.0)
                    diff += PI * 2.0;
                if (diff <= radians)
                    pointsInRange++;
                else done = true;
            }
            maxVisible = max(maxVisible, pointsInRange + alwaysVisible);
            pointsInRange = max(1, pointsInRange - 1); // we lose one point in range while going to the next one
        }
        return maxVisible;
    }
};
```

