/**
 * Time Complexity: O(n_points * log(n_points))
 * Space Complexity: O(n_points)
 * where `n_points` is the length of the vector `points`
 */
class Solution {
 public:
  int visiblePoints(const vector<vector<int>> &points,
                    const int angle,
                    const vector<int> &location) {
    constexpr double degrees_per_pi = 180.0;
    const double pi = atan2(0, -1);
    const double cycle = 2 * pi;
    vector<double> angles;
    int same_points = 0;
    for (const vector<int> &point : points) {
      if (point == location) {
        ++same_points;
      } else {
        angles.emplace_back(atan2(point.back() - location.back(), point.front() - location.front()));
      }
    }

    sort(angles.begin(), angles.end());
    const double covered_angle = pi * (angle / degrees_per_pi);
    const int n = static_cast<int>(angles.size());
    int begin = 0;
    int end = 0;
    for (; begin < n; ++end) {
      const double angle_between_begin_end = (end >= n ? cycle + angles[end - n] : angles[end]) - angles[begin];
      if (angle_between_begin_end > covered_angle) {
        ++begin;
      }
    }
    return same_points + (end - begin);
  }
};
```

