# define M_PI           3.14159265358979323846 
# define ANG(a)         (a > M_PI ? a - M_PI : a) 

class Solution {
public:
    int visiblePoints(vector<vector<int>>& points, int a, vector<int>& location) {
        int left = 0, right = 0, ans = 0, count = 0;

        vector<double> collect;
        for (auto& it: points) {
            if (it == location) {
                count ++;
                continue;
            }
            collect.push_back(get(location, it));
        }
        sort(collect.begin(), collect.end());

        double angle = (double)a / 180 * M_PI;
        for (; left < collect.size(); ++ left) {
            while (right < collect.size() && collect[right] - collect[left] <= angle) {
                ans = max(ans, right - left + 1);
                right ++;
            }
            if (right == collect.size()) break;
        }
        right = 0;
        for (; left < collect.size(); ++ left) {
            while (right < collect.size() && collect[right] - collect[left] + 2 * M_PI <= angle) {
                ans = max(ans, right + 1 + (int)collect.size() - left);
                right ++;
            }
            if (right == collect.size()) break;
        }

        return ans + count;
    }

    double get(vector<int>& a, vector<int>& b) {
        double store;

        if (a[0] == b[0]) {
            store =  b[1] >= a[1] ? M_PI / 2 : - M_PI / 2;
        } else {
            store = atan2(b[1] - a[1], b[0] - a[0]);
        }

        return store < 0 ? store + 2 * M_PI : store;
    }
};
