            if(x==points[i][0] && y==points[i][1]){
                same++;
                continue;
            }
            int dx=points[i][0]-x;
            int dy=points[i][1]-y;
            double ang = atan2(dy,dx) * 180.0 /M_PI;
                if(ang<0){
                    ang+=360;
                }
            angles.push_back(ang);
        }
        sort(angles.begin(),angles.end());
        int xi=angles.size();
        for(int i=0;i<xi;i++){
            angles.push_back(angles[i]+360);
        }
        int i=0;
        int j=0;
        int ans=0;
        while(j<angles.size()){
