class Solution:

    def getAngle(self,point1,point2):
        # get the counterclockwise angle of the vector from point1 to point2

        delta_y, delta_x = point2[1] - point1[1], point2[0] - point1[0]

        if delta_x > 0 and delta_y >= 0: # quadrant 1
            return math.atan(delta_y/delta_x)*180/math.pi
        elif delta_y >= 0 and delta_x < 0 : # quadrant 2
            return (math.pi - math.atan(-delta_y/delta_x))*180/math.pi
        elif delta_y <= 0 and delta_x < 0: # quadrant 3
            return (math.pi + math.atan(delta_y/delta_x))*180/math.pi
        elif delta_y <= 0 and delta_x > 0: # quadrant 4
            return (2 * math.pi - math.atan(-delta_y/delta_x))*180/math.pi
        elif delta_x == 0:
            if delta_y > 0:
                return 90
            else:
                return 270

    def visiblePoints(self, points: List[List[int]], angle: int, location: List[int]) -> int:

        angles = [] # use to store the polar angles of each point from location (pseudo-origin)
        origin = 0 # use to encode if origin point is included in points

        for point in points:
            if point == location:
                origin += 1
            else:
                angles.append(self.getAngle(location,point))

        for current_angle in angles:
            if current_angle > 180: # handle wraparound for angles > 180 (in 3rd and 4th quadrants)
                angles.append(- (360-current_angle) )

        angles.sort()

        i = 0
        max_points = 0

        for j in range(len(angles)):    
            while angles[j]-angles[i] > angle and i<j:
                i += 1

            max_points = max(max_points,j-i+1)

        return max_points + origin
