from queue import deque
class Solution:
    def visiblePoints(self, points: List[List[int]], angle: int, location: List[int]) -> int:
        safe_points = 0
        angles = []
        ans = 0

        for point in points:
            if(point == location):
                safe_points += 1
            else:
                # Get angle for all locations and sort the array
                angles.append(self.get_angle(location, point))

        angles.sort()

        # Using queue enter values and remove them if the condition is false
        q = deque()
        for a in angles:
            q.append(a)
            while(a - q[0] > angle):
                q.popleft()
            ans = max(ans, len(q))

        # Another loop to capture the remaining area
        for a in angles:
            a += 360
            q.append(a)
            while(a - q[0] > angle):
                q.popleft()
            ans = max(ans, len(q))

        return ans + safe_points

    def get_angle(self, location, point):
        angle = math.atan2(point[0] - location[0], point[1] - location[1]) / (2 * math.pi) * 360

        if angle < 0:
            angle += 360
        return angle
