class Solution {
    public int visiblePoints(List<List<Integer>> points, int angle, List<Integer> location) {

        //this is a sliding window problem
        //the key idea is to convert these points to angles and sweep across points
        //with a sliding window

        int overlap = 0;
        List<Double> list = new ArrayList<>(points.size());
        for (List<Integer> p : points) {
            if (p.get(0) == location.get(0) && p.get(1) == location.get(1)) overlap++;
            else 
                list.add(angle(p.get(1) - location.get(1), p.get(0) - location.get(0)));
        }
        Collections.sort(list);

        int max = 0, n = list.size();
        Deque<Double> dq = new ArrayDeque<>();

        for(int lower=0, upper=0; lower < n && dq.size() < n; upper++){
            dq.offerLast(list.get(upper%n));

            while(canRemove(lower, upper, n, angle, dq)){
                dq.pollFirst();
                lower++;
            }

            max = Math.max(max, dq.size());
        } 

        return max + overlap;       
    }

    private double angle(int dy, int dx) {
         double a = Math.toDegrees(Math.atan2(dy, dx));
        return a < 0 ? a + 360 : a;
    }

    private boolean canRemove(int lower, int upper, int n, int angle, Deque<Double> dq){
        double decidingAngle = dq.peekLast() - dq.peekFirst();
        decidingAngle = decidingAngle < 0 ? decidingAngle + 360 : decidingAngle;
        if(decidingAngle > angle) return true;
        return false;
    }

}
