class Solution {
public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        typedef complex<double> pt;
        vector<double> angles;
        angles.reserve(2 * points.size());
        pt origin(location[0], location[1]);
        int extra = 0;
        for (auto &v : points) {
            pt z(v[0], v[1]);
            if (z == origin) {
                extra++;
                continue;
            }
            z -= origin;
            double a = (arg(z) * 180.0 / M_PI);
            if (a < 0) a += 360;
            angles.push_back(a);
        }
        int l = 0, r = 0, n = angles.size(), ans = 0;
        sort(angles.begin(), angles.end());
        for (int i = 0; i < n; i++) {
            angles.push_back(angles[i] + 360);
        }
        n *= 2;
        while (r < n) {
            double diff = angles[r] - angles[l];
            if (diff <= angle) {
                ans = max(ans, r - l + 1);
                r++;
            } else {
                l++;
            }
        }
        return ans + extra;
    }
};
