#define all(c) c.begin(),c.end()
#define EPS 1e-6

typedef vector<int> vi;
typedef vector<vi> vvi;

float calcAngle(vi &a, vi &b){
    return atan2(b[1]-a[1],b[0]-a[0])*180/acos(-1);
}
class Solution {
public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location)      {
        vector<float> angles;
        int sameAsLoc = 0;
        for(auto point:points){
            if(location[0]==point[0] && location[1]==point[1])
                sameAsLoc++;
            else{
                float ang = calcAngle(location,point);
                angles.push_back( ang );
                angles.push_back( 360 + ang );
            }
        }
        sort(all(angles));
        int p = 0, x = 0, y = 0;
        int ans = sameAsLoc;
        for(;x<angles.size();x++,p--){
            while(  y<angles.size() && \
                  (angles[y] - angles[x] - angle) < EPS ){
                p++; y++;
            }
            ans = max(p+sameAsLoc,ans);
        }
        return ans;
    }
};

