class Solution {
private:
    const int x = 0;
    const int y = 1;
public:
    // Time complexity: O(nlogn) because of sorting
    // Space complexity: O(n)
    // NOTE: 0 degrees means you can see points in a line
    int visiblePoints(vector<vector<int>>& pts, int angle, vector<int>& loc) {
        vector<double> angles;
        int res = 0;
        int samePt = 0;

        // Case where we see the entire grid
        if (angle == 360) return pts.size();

        // For each point, convert it to angles in degrees (relative to [0, 0])
        for (vector<int> &pt : pts) {

            // off set the points so that our origin begins with (0, 0)
            int currX = pt[x] - loc[x];
            int currY = pt[y] - loc[y];

            // special case where point and origin are the same, always see
            if (currX == 0 && currY == 0) {
                samePt++;
                continue;
            }

            // case where points laying on the quadrant border
            if (currX == 0) {
                if (currY > 0) {
                    angles.push_back(90.0);
                } else if (currY < 0) {
                    angles.push_back(270.0);
                }
                continue;
            }
            if (currY == 0) {
                if (currX > 0) {
                    angles.push_back(0.0);
                } else if (currX < 0) {
                    angles.push_back(180.0);
                }
                continue;
            }

            // calculate the relative angle after shifting to origin
            double currAngle = atan((double)abs(currY) / (double)abs(currX));
            currAngle *= (180 / M_PI);

            if (currX > 0 && currY > 0) { // Q1
                angles.push_back(currAngle);
            } else if (currX < 0 && currY > 0) { // Q2
                angles.push_back(180.0 - currAngle);
            } else if (currX < 0 && currY < 0) { // Q3
                angles.push_back(180.0 + currAngle);
            } else if (currX > 0 && currY < 0) { // Q4
                angles.push_back(360.0 - currAngle);
            }
        }

        // All points are the same as origin
        if (angles.empty()) {
            return samePt;
        }

        sort(angles.begin(), angles.end()); // small to large, O(nlogn) time complexity

        // get initial j, where j represents the first point we see in vision
        double jStartAngle = angles[0] - (double)angle;
        int j = 0;
        if (jStartAngle < 0) {
            jStartAngle += 360.0;
        }
        for (int k = 0; k < angles.size() - 1; k++) {
            if (angles[k] < jStartAngle && angles[k + 1] >= jStartAngle) {
                j = k + 1;
                break;
            }
        }

        // For each angle, check how many points we can see.
        // Note that two pointer approach is used to determine how many points are in vision
        for (int i = 0; i < angles.size(); i++) {

            double visionStart = angles[i] - (double)angle;
            if (visionStart < 0) {
                visionStart += 360.0;
            }

            while ((angles[i] > visionStart && (angles[j] < visionStart || angles[j] > angles[i])) ||
                   (angles[i] < visionStart && (angles[j] > angles[i] && angles[j] < visionStart)) ||
                   (angles[i] == visionStart && angles[j] != angles[i])) {
                if (++j == angles.size()) j = 0;
            }

            int currRes;
            if (j > i) {
                currRes = angles.size() - j + i + 1;
            } else {
                currRes = (i - j) + 1;
            }

            res = max(res, currRes);
        }

        return res + samePt;
    }
};
