func visiblePoints(points [][]int, angle int, location []int) int {
    origin := 0
    // 求各点相对于location的极角度， 并排序
    res := []float64{}
    for _, point := range points {
        if point[0] == location[0] && point[1] == location[1] {
            // 反正切值 (-π/2,π/2)
            origin++
        } else {
            res = append(res, math.Atan2(float64(point[1]-location[1]), float64(point[0]-location[0])))
        }
    }

    sort.Float64s(res)

    n := len(res)
    for i:=0;i<n;i++ {
        res = append(res, res[i]+2*math.Pi)
    }


    maxCnt, degree := 0, float64(angle) * math.Pi / 180
    for start, polarAngle := range res[:n] {
        end := sort.Search(n*2, func(j int) bool { return res[j] > polarAngle + degree })
        if end-start > maxCnt {
            maxCnt = end - start
        }
    }
    return origin + maxCnt
}


