class Solution {
    double get_angle(int x_diff, int y_diff) {
        return atan2(y_diff, x_diff) * 180 / M_PI;
    }
public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        //find degrees for points
        map<double,int> pts;
        int overlap=0;
        for (int i=0;i<points.size();i++){
            int x=points[i][0]-location[0];
            int y=points[i][1]-location[1];
            if (x==0 &&y==0) overlap++;
            else{
                double d=get_angle(x,y);
                //if (d<-180) d+=360;
                pts[d]++;   
            }
        }

        //find a range(direction) that can see max num of points
        bool lsecond=false;//use flags to record if it is first or second round
        bool rsecond=false;
        auto left=pts.begin(), right=pts.begin();
        int maxnum=0;
        int sum=0;
        while (right!=pts.end()){
            sum+= right->second;
            double diff= right->first- left->first;
            if (rsecond) diff+=360.0;
            if (lsecond) diff-=360.0;
            while (diff>angle){
                sum-=left->second; 
                left++; 
                if (left==pts.end()) break;
                diff= right->first- left->first;
                if (rsecond) diff+=360.0;
                if (lsecond) diff-=360.0;
            }
            maxnum= max(maxnum, sum);
            right++;

            if (left==pts.end()&& !lsecond){ left=pts.begin(); lsecond=true;}
            if (right==pts.end()&& !rsecond){ right=pts.begin(); rsecond=true;}
        }
        return maxnum+overlap;
    }
};
