class Solution 
{
	public:
		int visiblePoints(vector<vector<int> > const &points, int angle, vector<int> const &location) 
		{
			size_t n = points.size();
			map<double, size_t> angleCounts;
			int nPointsAtLocation = 0;

			// calculate theta angles and store in angleCount map
			for (size_t idx = 0; idx < n; ++idx)
			{
				// if point is at location -> this can be seen at all times, store in nPointsAtLocation
				if (points[idx][0] == location[0] && points[idx][1] == location[1])
				{
					++nPointsAtLocation;
					continue;
				}

				double length = sqrt(pow(points[idx][0] - location[0], 2) + 
                                     pow(points[idx][1] - location[1], 2));
				double theta = acos((points[idx][0] - location[0]) / length) * 180 / M_PI;

				// if point y-coordinate is lower than location y-coordinate, angle is between 180-160 degrees
				if (points[idx][1] < location[1])
					theta = 360 - theta;

				// round theta to certain precision
				theta = round(1e5 * theta) / 1e5;

				// store theta angle in map and update frequency of that angle
				angleCounts[theta] += 1;
			}

			// iterate theta angles in map, and build cumulative count map
			map<double, int> angleCumCounts;
			int currCount = 0;
			for (auto it : angleCounts)
			{
				double theta = it.first;
				currCount += it.second;
				angleCumCounts[theta] = currCount;
			}

			// max number of points stored in angleCumCounts
			int maxAngleCumCount = (angleCumCounts.size() == 0) ? 0 : prev(angleCumCounts.end())->second;

			int maxNPoints = 0;
			for (auto it : angleCumCounts)
			{
				double theta = it.first;
				int currCount = it.second;

				int refCount = 0;
				if (theta - angle >= 0)
				{
					// answer is angleCumCounts[theta] - angleCumCounts[theta - angle]
					// lower_bound will find matching theta angle equal to or larger than requested theta - angle
					// this defines the longest sliding window possible
					double refTheta = theta - angle;
					auto ref_it = angleCumCounts.lower_bound(refTheta);

					// include points at lowest possible theta angle -> go one iteration back before subtraction
					if (ref_it != angleCumCounts.begin())
					{
						--ref_it;
						refCount = ref_it->second;
					}

					maxNPoints = max(maxNPoints, currCount - refCount);
				}
				else
				{
					// answer is angleCumCounts[theta] + maxAngleCumCount - angleCumCounts[360 + theta - angle]
					double refTheta = 360 + theta - angle;
					auto ref_it = angleCumCounts.lower_bound(refTheta);

					if (ref_it != angleCumCounts.begin())
					{
						--ref_it;
						refCount = ref_it->second;
					}

					maxNPoints = max(maxNPoints, currCount + maxAngleCumCount - refCount);
				}
			}

			return maxNPoints + nPointsAtLocation;
		}
};

