public class Solution {
    public int VisiblePoints(IList<IList<int>> points, int angle, IList<int> location) {
        List<double> store = new List<double>();
        int result = 0;
        foreach(IList<int> point in points) {
            int dx = point[0] - location[0];
            int dy = point[1] - location[1];

            if (dx == 0 && dy == 0) {
                result ++;
                continue;
            }
            double angleRad = Math.Atan2(dy, dx) * (180.0 / Math.PI);
            store.Add(angleRad);
        }
        store.Sort();

        List<double> temp = new List<double>(store);

        foreach(double angleT in store) {
            temp.Add(angleT + 360.0);
        }
        int finalResult = result;
        for(int i=0, j=0; i< temp.Count(); i++) {
            while(temp[i] - temp[j] > angle) {
                j++;
            }
            finalResult = Math.Max(finalResult, result + i - j + 1);
        }
        return finalResult;
    }
}

