import ( 
    "sort"
    "math"
)
func visiblePoints(points [][]int, angle int, location []int) int {
    slopes,samePoint:=CalculateAngles(points,location)
    sort.Slice(slopes,func(i,j int)bool{
        return slopes[i] < slopes [j]
    })
    //change angle to radian angle
    radAngle := (float64(angle)*math.Pi)/180
    start:=0
    count:=0 
    maxCount:=0
    //Now we got all the angles use sliding window to check how many points we can have at our field of view
    for i:=0;i<len(slopes);i++{
        if slopes[i] <= slopes[start] + radAngle {
            count++
        }else {
            start++
            for start < len(slopes) {
                if slopes[i] <= slopes[start] + radAngle {
                    break
                }
                start++
            }
            count = i - start + 1
        }
        maxCount = max(maxCount,count)
    }
    //Add samePoint -> if location and points[i] are same include it
    return maxCount + samePoint
}

func max(i,j int)int {
    if i > j {
        return i
    }
    return j
}


func CalculateAngles(points [][]int,location []int)([]float64,int){
    slopes:=[]float64{}
    x:=location[0]
    y:=location[1]
    count:=0
//Find the All angles made by lines consisting of location and point[i]
//Formula tan0 = (y2-x1)/x2-x1
// To find angle that is theta use inverse tan function i.e atan2
    for _,val :=range points {
        diffY:=float64(val[1]-y)
        diffX:=float64(val[0]-x)
        if x == val[0] && y == val[1]{
            count++
        }else {
            slope:=math.Atan2(diffY,diffX)
            slopes = append(slopes,slope)    
        }
    }
//Duplicate the array to make a circular array.So that point we can also start from 4th quad to reach 1st quad 
    for _,val := range slopes{
        dup:=val + 2*math.Pi
        slopes = append(slopes,dup)
    }
    return slopes,count
}

