class Solution {
public:
    int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
        vector<long double> angles;
        long long x=location[0], y=location[1];
        long double pi=2*acos(0);
        int ans=0;
        for(int i=0;i<points.size();i++){
            long long x1=points[i][0], y1=points[i][1];
            if(x1==x && y1==y)ans++;
            else if(x1==x && y1>y){
                angles.push_back(90);
            } else if(x1==x && y1<y){
                angles.push_back(270);
            } else if(y1==y && x1>x){
                angles.push_back(0);
            } else if(y1==y && x1<x){
                angles.push_back(180);
            } else if(x1>x && y1>y){
                angles.push_back(180.0/pi*atan((long double)abs(y1-y)/abs(x1-x)));
            } else if(x1<x && y1>y){
                angles.push_back(180-180.0/pi*atan((long double)abs(y1-y)/abs(x1-x)));
            } else if(x1<x && y1<y){
                angles.push_back(180+180.0/pi*atan((long double)abs(y1-y)/abs(x1-x)));
            } else if(x1>x && y1<y){
                angles.push_back(360-180.0/pi*atan((long double)abs(y1-y)/abs(x1-x)));
            }
        }
        sort(angles.begin(),angles.end());
        int max1=(angles.size()>0);

        for(int i=0;i<angles.size();i++){
            if(angles[angles.size()-1]<=angles[i]+angle+0.00001){
                if(angles[i]+angle+0.00001<360){
                    max1=max((long long)max1,(long long)angles.size()-i);
                }
                else {
                    if(i==0){
                        return points.size();
                    }
                    long double an=angles[i]+angle+0.00001-360;
                    auto itr=upper_bound(angles.begin(),angles.end(),an);
                    max1=max((long long)max1,(long long)angles.size()-i+(long long)(itr-angles.begin()));
                }
            } else {
                auto itr=upper_bound(angles.begin(),angles.end(),angles[i]+angle+0.00001);
                max1=max((long long)max1,(long long)(itr-angles.begin()-i));
            }
        }

        return ans+max1;
    }
};
```

