class Solution:
  def visiblePoints(self, points: List[List[int]], angle: int, location: List[int]) -> int:
    # Points that are same as this location will always be visible, remove them
    num_points_same = len([p for p in points if p == location])
    points = [p for p in points if p != location]

    # Sort points by their polar angles with respect to east direction
    angles = sorted([self.get_angle(location, pt) for pt in points])

    # Initialize window ends 
    window_start = 0
    window_end = 0

    max_window_len = 0
    n = len(angles)
    while window_start < n: # The window can wrap around the end
      # Increment start until the span of the window is within limits
      while self.span(angles[window_start % n], angles[window_end % n]) > angle:
        window_start += 1
      window_len = window_end - window_start + 1
      max_window_len = max(max_window_len, window_len)
      window_end += 1
      # Window length cannot exceed n otherwise we are repeating some points
      if window_end - window_start >= n:
        window_start += 1
    return num_points_same + max_window_len

  def span(self, angle1, angle2):
    if angle1 <= angle2:
      return angle2 - angle1
    return 360 - (angle1 - angle2)

  def get_angle(self, p1, p2):
    diff = [p2[0] - p1[0], p2[1] - p1[1]]
    if diff[0] == 0:
      return 90 * (1 if diff[1] > 0 else -1)
    if diff[1] == 0:
      return 0 if diff[0] > 0 else 180
    angle = atan(abs(diff[1]) / abs(diff[0])) * 180 / pi
    if diff[0] > 0 and diff[1] > 0:
      return angle
    if diff[0] > 0 and diff[1] < 0:
      return -angle
    if diff[0] < 0 and diff[1] > 0:
      return (180 - angle)
    if diff[0] < 0 and diff[1] < 0:
      return -(180 - angle)
    return angle
