    def visiblePoints(self, points: List[List[int]], angle: int, location: List[int]) -> int:
        angles = []
        amount = 0
        same = 0
        # O(n)
        for x, y in points:
            dx = x - location[0]
            dy = y - location[1]
            if dx == 0 and dy == 0:
                same += 1
            else:
                degree = math.atan2(dx, dy) * (180 / math.pi)
                angles.append(degree)

        # O(nlogn)
        angles.sort()
        start, slider = 0, 0

        # O(n)
        for a in list(angles):
            angles.append(a+360)

        # O(n)
        while slider < len(angles):
            if angles[slider] - angles[start] <= angle:
                slider += 1
            else:
                start += 1
            amount = max(amount, slider - start + same)

        return max(amount, same)
