public class Solution {
    public int VisiblePoints(IList<IList<int>> points, int angle, IList<int> location) {

        int pointsAtOrigin;

        List<double> angles = getSortedListOfAngles(points, location, out pointsAtOrigin);

        int max = getMaxAnglesInGivenDiapason(angles, angle);

        return max + pointsAtOrigin;
    }

    private int getMaxAnglesInGivenDiapason(List<double> angles, int angle) {

        if (angles.Count == 0)
            return 0;

        int max = 0;
        int left = 0;
        int right = 0;
        int currentCount = 1;

        while (left != angles.Count-1) {

            double leftValue = angles[left];
            double currentLimit = leftValue + angle;
            double sortedRightIndex = getSortedRightIndex(right, angles);

            while (sortedRightIndex <= currentLimit) {
                currentCount++;
                right++;

                sortedRightIndex = getSortedRightIndex(right, angles);
            }
            currentCount--;
            max = Math.Max(max, currentCount);
            left++;
        }
        return max;
    }

    private List<double> getSortedListOfAngles(IList<IList<int>> points, IList<int> location, out int pointsAtOrigin) {

        List<double> angles = new List<double>();

        pointsAtOrigin  = 0;      

        for (int i=0; i<points.Count; i++) {

            var currentPoint = points[i];

            if (currentPoint[0] == location[0] && currentPoint[1] == location[1]) {
                pointsAtOrigin++;
                continue;
            }

            int triangleXLength = currentPoint[0] - location[0];
            int triangleYLength = currentPoint[1] - location[1];

            double degrees = Math.Atan2(triangleXLength,triangleYLength) * (180.0 / Math.PI);

            angles.Add(degrees);
        }

        angles.Sort();

        return angles;
    }

    private double getSortedRightIndex(int right, List<double> angles) {
        if (right <angles.Count)
            return angles[right];

        return 360 + angles[right - angles.Count];
    }
}
