const long double PI = 3.14159265;

int visiblePoints(vector<vector<int>>& points, int angle, vector<int>& location) {
    vector <long double> degrees;
    int originCount = 0;
    for(auto each : points){
        if(each == location)originCount++;
        else degrees.push_back(atan2(each[1] - location[1], each[0] - location[0]) * 180.0 / PI);
    }
    sort(degrees.begin(), degrees.end());
    degrees.insert(degrees.end(), degrees.begin(), degrees.end());
    int n = degrees.size(), l = 0, r = 0, result = 0;
    while(l < n/2 && r < n){
        if(degrees[r] - degrees[l] + ((r >= n/2) ? 360 : 0) <= (long double)angle){
            result = max(result, r-l+1);
            r++;
        }
        else l++;
    }
    return result + originCount;
}

