import (
    "math"
    "sort"
)

func visiblePoints(points [][]int, angle int, location []int) int {
    r2d := float64(180) / math.Pi
    l := len(points)

    if angle < 0 { angle *= -1 }
    if angle == 360 { return l }

    /* turn all points into degrees against location, exclude points at locations */
    degrees := make([]float64, 0, l * 2)
    result := 0

    for _, p := range points {
        if p[0] == location[0] && p[1] == location[1] {
            result++
            continue
        }

        d := math.Atan2(
            float64(p[1] - location[1]),
            float64(p[0] - location[0]),
        ) - math.Pi // [-2Pi, 0]
        d *= r2d
        degrees = append(degrees, d)
    }

    sort.Float64s(degrees)

    l = len(degrees)

    /* append values range from [0, 2Pi] */
    for i := 0; i < l; i++ {
        degrees = append(degrees, degrees[i] + 360)
    }

    l *= 2
    pf, pb := 0, 0
    max := 0

    for pf < l {
        cut := degrees[pf] - float64(angle)

        if degrees[pb] >= cut {
            if pf - pb + 1 > max { max = pf - pb + 1 }
            pf++
            continue
        }

        for pb <= pf && degrees[pb] < cut  {
            pb++
        }

        pf++
    }

    return result + max
}
