import java.lang.*;
class Solution {
    public int org_point = 0;
    public int visiblePoints(List<List<Integer>> points, int angle, List<Integer> location) {
         List<Double> radian_list = new ArrayList<>();
         for(List<Integer> point: points){
             double val = get_radian(point, location);
             if(val != -2){
                 radian_list.add(val);
             }
         }
         Collections.sort(radian_list);
         int size = radian_list.size();
         for(int i=0;i<size;i++){
             radian_list.add(radian_list.get(i) + 2*Math.PI);
         }

         double threshold = (angle/180.0)*Math.PI;
         int start = 0;
         int max_count = 0;
         for(int i=0;i<radian_list.size();i++){
             double cur_radian = radian_list.get(i);
             while(cur_radian - radian_list.get(start)>threshold+0.00000001){
                 start++;
             }
             max_count = Math.max(max_count, i-start+1);
         }



         return max_count + org_point;

    }


    public double get_radian(List<Integer> input, List<Integer> location){
        List<Integer> point = new ArrayList<>();
        double x = input.get(0) - location.get(0);
        double y = input.get(1) - location.get(1);
        //System.out.println("y: " + y);
        if(x==0 && y==0){
            org_point++;
            return -2;
        }
        double cos_val = x/Math.sqrt(x*x+y*y);
        double radian = Math.acos(cos_val);
        if(y<0){
            return -radian;
        }

        return radian;
    }
}
