class Solution {
    // Calculating compatibility scores of ith student and jth mentor
    int cal(int i,int j,vector<vector<int>>& arr1,vector<vector<int>>& arr2){
        int cnt=0;
        for(int k=0;k<arr1[0].size();k++){
            if(arr1[i][k]==arr2[j][k]){
                cnt++;
            }
        }
        return cnt;
