		if (mask == all_visited) {
			dp[pos][mask] = 0;
			return 0;
		}

		if (dp[pos][mask] != -1)
			return dp[pos][mask];


		int max1 = 0;
		for (int i = 0; i < m; i++) {   // try to find best matching between student and mentor

			if ((mask & (1 << i)) == 0) {

				int cal = 0;            // used for calculating how many answers matched in student and mentor
				for (int k = 0; k < n; k++) {
					if (students[pos][k] == mentors[i][k])
						cal++;
				}

				max1 = Math.max(max1, cal + fun(pos + 1, (mask | (1 << i)), students, mentors, m, n));
			}
		}

		dp[pos][mask] = max1;
		return max1;
	}


