class Solution {
public:
    int maxCompatibilitySum(vector<vector<int>>& stud, vector<vector<int>>& ment) {
        int r=stud.size(),c=stud[0].size(),ans=0,i=0;
        vector<int> idx;
        while(i<r)
        {
            idx.push_back(i);
            i++;
        }
        do
        {
            int curr=0;
            i=0;
            while(i<r)
            {
                int j=0;
                while(j<c)
                {
                    curr+=(stud[idx[i]][j]==ment[i][j]);
                    j++;
                }
                i++;
            }
            ans=max(ans,curr);
        }while(next_permutation(idx.begin(),idx.end()));
        return ans;
    }
};
