The idea is simple :-
1) I made a 2D matrix of size M*M for every possible combination.
2) then find "Maximum sum of a Matrix where each value is from a unique row and column".

```
class Solution {
public:
    vector<int> col;
    int mx = INT32_MIN;
    void maxSum(int n, vector<vector<int>> &matrix, int idx, int sum)
    {
        if (idx == n)
        {
            mx = max(mx, sum);
            return;
        }
        for (int i = 0; i < n; i++)
        {
            if (!col[i])
            {
                col[i] = true;
                maxSum(n, matrix, idx + 1, sum + matrix[idx][i]);
                col[i] = false;
            }
        }
    }
    void fillMatrix(vector<int>&s,vector<int>&m, int i, int j, vector<vector<int>>&matrix)
    {
        int x = 0;
        for(int k = 0; k<s.size(); k++)
        {
            if(s[k]==m[k])
                x++;
        }
        matrix[i][j] = x;
    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int m = students.size();
        vector<vector<int>>matrix(m,vector<int>(m,0));
        for(int i = 0; i<m; i++)
        {
            for(int j = 0; j<m; j++)
            {
                fillMatrix(students[i],mentors[j], i, j, matrix);
            }
        }
        col.assign(m, false);
        maxSum(m, matrix, 0, 0);
        return mx;
    }
};
```


