For every student try to pair it with every mentor, and for all these combinations, save the maximum resultant.
```
class Solution {
public:

void find(vector<vector<int> > &students,vector<vector<int >> &mentors,int st,int n,vector<int> &used,int &temp,int &ans)
{
    if(st == n)
    {
        ans = max(ans,temp);
        return;
    }

    for(int i = 0 ;i < n;i++)
    {
        if(used[i] == 0)//got a mentor thats still free, so pair it
        {
            used[i] = 1;
            int score = 0;
            for(int j = 0 ;j < students[st].size();j++)
            {
                if(students[st][j] == mentors[i][j])
                    score++;
            }
            temp += score;//add the score
            find(students,mentors,st+1,n,used,temp,ans);//recur for remaining students
            used[i] = 0;//backtrack
            temp -= score;
        }
    }
}
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {

        int n = students.size();
        vector<int> used(n,0);//to keep track of fee mentors
        int temp = 0;//temp ans for a specific combination
        int i = 0;//start with the first student
        int ans = 0;//final ans
        find(students,mentors,i,n,used,temp,ans);
        return ans;

    }
};
