            used[i] = 1;
            int score = 0;
            for(int j = 0 ;j < students[st].size();j++)
            {
                if(students[st][j] == mentors[i][j])
                    score++;
            }
            temp += score;//add the score
            find(students,mentors,st+1,n,used,temp,ans);//recur for remaining students
            used[i] = 0;//backtrack
            temp -= score;
        }
    }
}
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {

        int n = students.size();
        vector<int> used(n,0);//to keep track of fee mentors
        int temp = 0;//temp ans for a specific combination
        int i = 0;//start with the first student
        int ans = 0;//final ans
        find(students,mentors,i,n,used,temp,ans);
        return ans;

    }
};
