        vector<int> allot;
        for(int i=0;i<m;i++) allot.push_back(i);
        int ans=0;
        for(int i=0;i<m;i++) ans+=(com[i][allot[i]]);
        //Here we check every possible assignment of m students to m teachers
        do{  int temp=0;
            for(int i=0;i<m;i++) temp+=(com[i][allot[i]]);
           ans=max(ans,temp);

        }while(next_permutation(allot.begin(),allot.end()));
        return ans;

    }
}

