class Solution {
    Integer[][]dp;
    int m, n;
    int[][] S;
    int[][] M;
    public int maxCompatibilitySum(int[][] students, int[][] mentors) {
        m = students.length;
        n = students[0].length;
        S = students;
        M = mentors;
        dp = new Integer[m][1 << 8];
        return dfs(0, 0);
    }

    // m : no of students
    // n : no of Questions
    public int dfs(int i, int mask){
        if(i == m)
            return 0;

        if(dp[i][mask] != null) return dp[i][mask];

        int maxScore = 0;
        for (int j = 0; j < m; j++){
            if((mask & (1 << j)) == 0){
                int score = 0;
                for (int k = 0; k < n; k++){
                    score += S[i][k] == M[j][k] ? 1 : 0;
                }
                score += dfs(i + 1, mask | (1 << j));
                maxScore = Math.max(maxScore, score);
            }
        }
        dp[i][mask] = maxScore;
        return maxScore;
    }
}
