class Solution {
public:

    int calculate_score(vector<int> &a, vector<int> &b)
    {
        int ans = 0;
        for(int i = 0; i < a.size(); i++)
        {
            ans += (a[i] == b[i]);
        }
        return ans;
    }

    int mx_sum(int i, vector<bool> used, vector<vector<int>> &score, int n,int m)
    {
        if(i >= n) return 0;
        int ans = 0;
        for(int j = 0; j < m; j++)
        {
            if(used[j]) continue;
            used[j] = 1;
            ans = max(ans, score[i][j] + mx_sum(i + 1, used, score, n, m));
            used[j] = 0;
        }
        return ans;
    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) 
    {
        int n = students.size();
        int m = mentors.size();

        vector<vector<int>> score(n, vector<int> (m, 0));

        for(int i = 0; i < n; i++)
            for(int j = 0; j < m; j++)
                score[i][j] = calculate_score(students[i], mentors[j]);

        vector<bool> used(m, 0);
        return mx_sum(0, used, score, n, m);
    }

};
