const int N = 9;
int dp[N][1 << 8];
class Solution {
public:

    int calculate_score(vector<int> &a, vector<int> &b)
    {
        int ans = 0;
        for(int i = 0; i < a.size(); i++)
        {
            ans += (a[i] == b[i]);
        }
        return ans;
    }

    int mx_sum(int i, int mask, vector<vector<int>> &score, int n,int m)
    {
        if(i >= n) return 0;

        if(dp[i][mask] != -1) return dp[i][mask];

        int ans = 0;
        for(int j = 0; j < m; j++)
        {
            if(mask & (1 << j)) continue;
            ans = max(ans, score[i][j] + mx_sum(i + 1, mask + (1 << j), score, n, m));
        }
        return dp[i][mask] = ans;
    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) 
    {
        int n = students.size();
        int m = mentors.size();

        vector<vector<int>> score(n, vector<int> (m, 0));

        for(int i = 0; i < n; i++)
            for(int j = 0; j < m; j++)
                score[i][j] = calculate_score(students[i], mentors[j]);

        //global
        for(int i = 0; i < N; i++)
            for(int j = 0; j < (1 << 8); j++)
                dp[i][j] = -1;

        int mask = 0;
        return mx_sum(0, mask, score, n, m);
    }

};
