    int fun(vector<vector<int>>& students, vector<vector<int>>& mentors,int j,int mask){
        int n=mentors.size();
        if(j<0)
            return 0;
			// normal dp condition 
        if(dp[j][mask]!=-1)
            return dp[j][mask];
			// we will traverse for element and check the score for each students[i] and mentors[j] 
			// and choose if and only if i is not taken already which is represented by the mask
        for(int i=0;i<n;i++){
		// this is checking condition to check if i was already taken or not.   
		// eg. is mask was 6 then its binary representation is 110 and we want to check if i==2 is present or not 
		// i.e is there 1 at the 2nd index from right. hence we take & of 110 and 100(1<< i )
            if(mask&(1<<i)){
                dp[j][mask]=max(dp[j][mask],ch(students[i],mentors[j])+fun(students,mentors,j-1,mask^(1<<i)));
				//  because we are now using the i th array of students we have to mark its index in the mask as 0 --- 
				// mask^(1<<i) converts the index at i position to 0 
            }
        }
        return dp[j][mask];
    }
