        public int maxCompatibilitySum(int[][] students, int[][] mentors) {
            int n = students.length;
            return helper(0, 0, students, mentors, new Integer[n][1 << n]);
        }


        private int helper(int pos, int mask, int[][] students, int[][] mentors, Integer[][] memo) {
            int n = students.length;
            if (mask == (1 << n) - 1)
                return 0;
            if (memo[pos][mask] != null)
                return memo[pos][mask];

            int maxScore = 0;
            for (int i = 0; i < n; i++) {
                if ((mask & (1 << i)) == 0) {
                    int score = getScore(students[pos], mentors[i]);
                    maxScore = Math.max(maxScore, score + helper(pos + 1, (mask | (1 << i)), students, mentors, memo));
                }
            }

            return memo[pos][mask] = maxScore;
        }

        private int getScore(int[] a, int[] b) {
            int count = 0;
            for (int i = 0; i < b.length; i++)
                if (a[i] == b[i]) count++;
            return count;
        }
