        int ans=0;
        for(int i=0;i<sz;i++){
            if(!vis[i]){
                vis[i]=1;
                ans=max(ans,score(s[student],m[i])+solve(s,m,student+1,sz,vis)); // pass next student(student+1)
                vis[i]=0;
            }
        }
        return ans;
    }

    int maxCompatibilitySum(vector<vector<int>>& s, vector<vector<int>>& m) {
        int ans=0;
        vector<bool>vis(m.size(),0); // to keep track of already selected mentors
        for(int i=0;i<m.size();i++){
            vis[i]=1; // select ith mentor
            ans=max(ans,score(s[0],m[i])+solve(s,m,1,s.size(),vis)); // calculate score of student and selected mentor and then solve for other pairs
            vis[i]=0; // deselect(backtrack)
        }
        return ans;
    }
};
