from itertools import permutations

class Solution:
    def maxCompatibilitySum(self, students, mentors) -> int:
        def calc(lst1, lst2):
            res = 0
            for num1, num2 in zip(lst1, lst2):
                res += num1 == num2
            return res

        R = len(students)
        all_pairs = list(permutations(range(R)))

        res = 0
        for pair in all_pairs:
            score = 0
            for idx1, idx2 in enumerate(pair):
                score += calc(students[idx1], mentors[idx2])
            res = max(res, score)
        return res
