class Solution {
public:
    int ans = 0;
    int score(vector<int>&v1, vector<int>&v2, int m){
        int cnt = 0;
        for(int i=0; i<m; i++){
            cnt += (v1[i] == v2[i]);
        }
        return cnt;
    }
    void solve(int idx, vector<int>&arr, vector<vector<int>>& ss , vector<vector<int>>& mrs, int n, int m){
        if(idx == arr.size()){
            int sum = 0;
            for(int i=0; i<n; i++){
                sum += score(ss[i],mrs[arr[i]],m);
            }
            ans = max(ans,sum);
            return;
        }
        for(int i=idx; i<arr.size(); i++){
            swap(arr[idx],arr[i]);
            solve(idx+1,arr,ss,mrs,n,m);
            swap(arr[idx],arr[i]);
        }
    }

    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int n = students.size();
        int m = students[0].size();
        vector<int>arr(n);
        for(int i=0; i<n; i++) arr[i] = i;
        solve(0,arr,students,mentors,n,m);
        return ans;
    }
};
