        if(pos>=n) {
            return 0;
        }
        int mx = 0;
        for(int i=0; i<n; i++) {
            if(!visited[i]) {
                int cnt = 0;
                for(int j = 0; j < m; j++) {
                    if(students[pos][j] == mentors[i][j]) cnt++;
                }
                visited[i] = 1;
                mx = max(mx, cnt+permutation(students, mentors, pos+1, n, m));
                visited[i] = 0;
            }
        }
        return mx;
    }
};
