class Solution {
public:
    int dp[9][1<<9];
    int n,m;
    int maxCompatibilitySum(vector<vector<int>>& s, vector<vector<int>>& ma) {
        m=s.size(),n=ma[0].size();
        memset(dp,-1,sizeof(dp));
        return fun(m,0,s,ma);
    }
    int fun(int i,int mask,vector<vector<int>>& s,vector<vector<int>>& ma){
        if(i==0){
            return 0;
        }
        if(dp[i][mask]!=-1) return dp[i][mask];
        int ans=0;
        vector<int>temp=s[i-1];
        for(int j=0;j<m;j++){
            int mask1=mask&(1<<j);
            if(!mask1){
                int sum=0;
                for(int p=0;p<n;p++) sum+=(ma[j][p]==temp[p]);
                ans=max(ans,sum+fun(i-1,mask|(1<<j),s,ma));
            }
        }
        return dp[i][mask]=ans;
    }
};
