class Solution {
public:
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {

        vector<int> stuMask;
        vector<int> menMask;

        for(int i=0;i<students.size();i++){
            int mask=0;
            for(int j=0;j<students[i].size();j++){
                if(students[i][j]==1)
                    mask=mask|(1<<j);
            }
            stuMask.push_back(mask);
        }

        for(int i=0;i<mentors.size();i++){
            int mask=0;
            for(int j=0;j<mentors[i].size();j++){
                if(mentors[i][j]==1)
                    mask=mask|(1<<j);
            }
            menMask.push_back(mask);
        }

        vector<vector<int>> dp(students.size(),vector<int>(mentors.size(),0));
        for(int i=0;i<stuMask.size();i++){
            for(int j=0;j<menMask.size();j++){
                int val=0;
                for(int k=0;k<students[0].size();k++){
                    if((stuMask[i]&(1<<k)) == (menMask[j]&(1<<k))){
                        val++;
                    }
                }
                dp[i][j]=val;
            }
        }

        int ans=0;
        vector<bool> stuVisited(students.size(),false);
        helper(dp,ans,0,stuVisited,0);
        return ans;
    }

    void helper(vector<vector<int>> &dp,int &ans,int val,vector<bool> &stuVisited,int idx){

        if(idx==stuVisited.size()){
            ans=max(ans,val);
            return;
        }

        for(int i=0;i<stuVisited.size();i++){
            if(!stuVisited[i]){
                stuVisited[i]=true;
                helper(dp,ans,val+dp[i][idx],stuVisited,idx+1);
                stuVisited[i]=false;
            }
        }
    }
};
