//We have m different students and m different mentors. So, there are m! different ways of mapping students
//with the mentors. This is what I've done and at last took the maximum of it. This is kind of brute force but 
//before that I converted every row to a binary number because bitwise operations are very fast

class Solution {
public:
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int m = students.size();
        int n = students[0].size();

        /*Converting the 2D vector to a 1D vector*/
        vector<int> st(m);
        vector<int> me(m);
        for(int i=0; i<m; i++){
            int num1 = 0, num2 = 0;
            for(int j=0; j<n; j++){
                num1*=2;
                num1+=students[i][j];
                num2*=2;
                num2+=mentors[i][j];
            }
            st[i] = num1; //student
            me[i] = num2; //mentor
        }
        /*I've converted every row to a number whose binary representation is same as that of the row*/

        vector<int> perm(m); //created an array to map students to mentors
        for(int i=0; i<m; i++) perm[i] = i; //first permutation -> 0,1,2,3,...,m
        int res = 0; //result = maximum of all scores obtained from m! different permutations (or mappings)
        do{
            //this is a permutation
            int score = 0;
            for(int i=0; i<m; i++){
                int k = st[i]^me[perm[i]]; //xor between student and mentor will turn OFF matching bits
                //there are n bits in total and suppose p out of them are not matching
                //so the score = (n-p)
                score+=(n - __builtin_popcount(k));
                //__builtin_popcount(...) is an inbuilt function to count the number of ON bits in a number
            }
            res = max(score, res);
        }
        while(next_permutation(perm.begin(), perm.end())); //we iterate to the next permutation (or mapping)

        return res;
    }
};
```

