class Solution {
public:    
    int helper(vector<vector<int>>& s, vector<vector<int>>& me, int mask, vector<int>& dp){
        int idx = bitset<8>(mask).count(), m = s.size(), n = s[0].size();
        if(idx>=m) return 0;
        if(dp[mask]!=-1) return dp[mask];
        int ans=0;
        for(int i=0; i<m; i++){
            if(mask&(1<<i))continue;
            int cnt=0;
            for(int j=0; j<n; j++){
                if(s[idx][j]==me[i][j]) cnt++;
            }
            ans=max(ans,cnt+helper(s, me, mask|(1<<i), dp));
        }
        return dp[mask]=ans;
    }

    int maxCompatibilitySum(vector<vector<int>>& s, vector<vector<int>>& me) {
        vector<int> dp(256/*2^8*/, -1);
        return helper(s, me, 0, dp);
    }
};
