```public class Solution {
    public int MaxCompatibilitySum(int[][] students, int[][] mentors) 
    {
         int rows = students.Length;
            int cols = students[0].Length;

            int[,] compatability = new int[rows, rows];

            for (int i = 0; i < rows; i++) //student
            {
                int[] s = students[i];
                for (int j = 0; j < rows; j++) //mentor
                {
                    int[] t = mentors[j];

                    int comp = 0;
                    for (int k = 0; k < cols; k++)
                    {
                        if (s[k] == t[k]) comp++;
                    }

                    compatability[i, j] = comp;
                }
            }

            int maxsum = 0;
            return MaxCompatibilitySumDFS(compatability, 0, new bool[rows], ref maxsum, 0);
    }

    public  int MaxCompatibilitySumDFS(int[,] compatability, int i,  bool[] visited, ref int maxsum, int sum)
        {
            int n = compatability.GetLength(0);

            maxsum = Math.Max(sum, maxsum);
            if (i >= n) return maxsum;

            for (int j = 0; j < n; j++)
            {
                if (visited[j]) continue;
                int sum1 = sum + compatability[i, j];              
                visited[j]=true;
                maxsum = MaxCompatibilitySumDFS(compatability, i + 1, visited, ref maxsum, sum1);
                visited[j] = false;
            }

            return maxsum;
        }
