class Solution {
public:
    int maxCompatibilitySum(vector<vector<int>>& students, vector<vector<int>>& mentors) {
        int res = INT_MIN, n = students.size(), m = students[0].size();

        vector<bool> visited(n, false);
        f(0, 0,students, mentors, visited, res, n, m);

        return res;
    }

    void f(int start, int sum, vector<vector<int> > &students, vector<vector<int>  >&mentors, vector<bool> &visited, int &res, int n, int m) {
        if(start == n) {
            res = max(res, sum);
            return;
        }

        for(int i = 0; i < n; i++) {
            if(!visited[i]) {
                int count = 0;
                for(int j = 0; j < m; j++) {
                    if(students[start][j] == mentors[i][j])
                        count++;
                }
                visited[i] = true;
                f(start+1, sum+count, students, mentors, visited, res, n, m);
                visited[i] = false;
            }
        }
    }
};
