class Solution {
    private int max;

    public int maxCompatibilitySum(int[][] students, int[][] mentors) {
        max = 0;
        permute(students, mentors, 0, students.length);
        return max;
    }

    private void permute(int[][] students, int[][] mentors, int index, int N){
        if(index == N){
            int score = 0;
            for(int i = 0; i < N; i++){
                score += compatibility(students[i], mentors[i]);
            }

            max = Math.max(max, score);
        }else{
            for(int i = index; i < N; i++){
                swap(students, index, i);
                permute(students, mentors, index + 1, N);
                swap(students, index, i);
            }
        }
    }

    private void swap(int[][] students, int i, int j){
        int[] temp = students[i];
        students[i] = students[j];
        students[j] = temp;
    }

    private int compatibility(int[] a, int[] b){
        int i, N = a.length;
        int sum = 0;

        for(i = 0; i < N; i++){
            if(a[i] == b[i]){
                ++sum;
            }
        }

        return sum;
    }
}
