    void solve(int[][]students,int[][]mentor,String s){

	//find the ans from each permutation and return the ,max answer
        if(students.length==set.size()){
            int sum=0;
            for(int i=0;i<students.length;i++){

                int p=0;
                int[]z=students[i];
                int[]z1=mentor[s.charAt(i)-'0'];

                for(int j=0;j<z.length;j++){
                    if(z[j]==z1[j]){
                        p++;
                    }
                }
                sum=sum+p;
            }
            max=Math.max(max,sum);
            return;
        }
		// Calculate all permutations
        for(int i=0;i<students.length;i++){
            if(set.contains(i)){
                continue;
            }
            set.add(i);
            solve(students,mentor,s+i);
            set.remove(i);
        }

    }
}
