class Solution {
public:
    inline int score(vector<int> &a, vector<int> &b, int &size)
    {
        int s=0;
        for(int i=0;i<size;++i)
            if(a[i]==b[i])
                s++;
        return s;
    }
    inline void solve(int arr[], vector<bool> &a, int &m, int &n, int &ms, int l, int sum)
    {
        if(l==m)
            ms=max(ms,sum);
        else if(sum+((m-l)*n)>ms)
        {
            for(int i=0;i<m;++i)
            {
                if(a[i])
                {
                    a[i]=0;
                    solve(arr,a,m,n,ms,l+1,sum+arr[(l*m)+i]);
                    a[i]=1;
                }
            }
        }
    }
    inline int maxCompatibilitySum(vector<vector<int>>& stu, vector<vector<int>>& men) 
    {
        ios::sync_with_stdio(0);
        cin.tie(0);
        cout.tie(0);

        int ms=0,m=stu.size(),n=stu[0].size(),t=m*m;
        vector<bool> a(m,1);
        int *arr=new int[t];

        for(int i=0;i<m;++i)
            for(int j=0;j<m;++j)
                arr[(i*m)+j]=score(stu[i],men[j],n);

        solve(arr,a,m,n,ms,0,0);
        return ms;
    }
};
