class Solution {
    public int maxCompatibilitySum(int[][] students, int[][] mentors) {
        int[][] scores = new int[students.length][students.length];
        for (int i = 0; i < scores.length; i++) {
            for (int j = 0; j < scores[0].length; j++) {
                scores[i][j] = getCompatibilityScore(students[i], mentors[j]);
            }
        }
        boolean[][] states = new boolean[2][students.length];
		// Use list of indexes to represent whethe current state has been visited or not
        Map<List<Integer>, Integer> cache = new HashMap<>();
        return getMaxScore(scores, cache, states);
    }

    private int getMaxScore(int[][] scores, Map<List<Integer>, Integer> cache, boolean[][] states) {
        List<Integer> indexes = getIndexes(states);
		// If visited, directly return
        if (cache.containsKey(indexes)) {
            return cache.get(indexes);
        }
        // there is only one element
        if (indexes.size() == 2) {
            cache.put(indexes, scores[indexes.get(0)][indexes.get(1)]);
            return cache.get(indexes);
        }
        // there are a lot element
        int currMax = 0;
        for (int i = 0; i < indexes.size() / 2; i++) {
            int row = indexes.get(0);
            states[0][row] = true;
            for (int j = 0; j < indexes.size() / 2; j++) {
                int col = indexes.get(j + indexes.size() / 2);
                states[1][col] = true;
                currMax = Math.max(currMax, scores[row][col] + getMaxScore(scores, cache, states));
                states[1][col] = false;
            }
            states[0][row] = false;
        }
        cache.put(indexes, currMax);
        return cache.get(indexes);
    }

    private List<Integer> getIndexes(boolean[][] states) {
        List<Integer> indexes = new ArrayList<>();
        for (int i = 0; i < states.length; i++) {
            for (int j = 0; j < states[0].length; j++) {
                if (states[i][j] == false) {
                    indexes.add(j);
                }
            }
        }
        return indexes;
    }

    private int getCompatibilityScore(int[] student, int[] mentor) {
        int sum = 0;
        for (int i = 0; i < student.length; i++) {
            if (student[i] == mentor[i]) {
                sum++;
            }
        }
        return sum;
    }
}
