class Solution:
    def maxCompatibilitySum(self, students: List[List[int]], mentors: List[List[int]]) -> int:
        m = len(students)
        n = len(students[0])
        complete = (1 << m) - 1
        scores = [[0] * m for _ in range(m)]
        def calc(st, me):
            score = 0
            for i in range(n):
                if st[i] == me[i]:
                    score += 1
            return score
        for i in range(m):
            for j in range(m):
                scores[i][j] = calc(students[i], mentors[j])
        @lru_cache(None)
        def dfs(mask1, mask2):
            if mask1 == complete:
                return 0
            res = 0
            for i in range(m):
                if (mask1 >> i) & 1:
                    continue
                for j in range(m):
                    if (mask2 >> j) & 1:
                        continue
                    res = max(res, dfs(mask1 ^ (1 << i), mask2 ^ (1 << j)) + scores[i][j])
            return res
        res = dfs(0, 0)
        dfs.cache_clear()
        return res
