            for(int i = 0; i<n;i++){
                for(int j = 0; j<m; j++){
                    if(students[i][j]==mentors[pos[i]][j])cur++;
                }
            }
            ans = max(cur,ans);
        }while(next_permutation(pos.begin(), pos.end()));
        return ans;
    }
